<link rel="stylesheet" href="https://unpkg.com/leaflet@1.0.1/dist/leaflet.css" />
<div id="map" style="height: 600px; width: 100%"></div>

<script type="text/javascript">
function initMap() {
    var map = L.map('map', { zoomControl:false, scrollWheelZoom: false, dragging: false}).setView([49.83798, 9.84375], 4);
    var Esri_WorldStreetMap = L.tileLayer('http://server.arcgisonline.com/ArcGIS/rest/services/World_Street_Map/MapServer/tile/{z}/{y}/{x}', {
  	attribution: 'Tiles &copy; Esri &mdash; Source: Esri, DeLorme, NAVTEQ, USGS, Intermap, iPC, NRCAN, Esri Japan, METI, Esri China (Hong Kong), Esri (Thailand), TomTom, 2012'
   });
   Esri_WorldStreetMap.addTo(map);

    var geoMarkers = L.featureGroup().addTo(map);
    var el = angular.element($('#map').parent('.ng-scope'));
    angular.element(el).ready(function() {
        window.locationWatcher = el.scope().compiledScope.$watch('locations', function(newValue, oldValue) {
            geoMarkers.clearLayers(); // -- if you want to only show new data clear the layer first
            locations = newValue
            angular.forEach(newValue, function(city) {
                var marker = L.marker([ city.lat,city.lon ])
                  .bindPopup(city.name)
                  .addTo(geoMarkers);
            });
            map.fitBounds(geoMarkers.getBounds());
        })
    });
}

if (window.locationWatcher) {
    // clear existing watcher otherwise we'll have duplicates
    window.locationWatcher();
}

// ensure we only load the script once, seems to cause issues otherwise
if (window.L) {
    initMap();
} else {
    console.log('Loading Leaflet library');
    var sc = document.createElement('script');
    sc.type = 'text/javascript';
    sc.src = 'https://unpkg.com/leaflet@1.0.1/dist/leaflet.js';
    sc.onload = initMap;
    sc.onerror = function(err) { alert(err); }
    document.getElementsByTagName('head')[0].appendChild(sc);
}
</script>

// Load the data
case class CityData(name: String, country: String, lat: Double, lon: Double)
val cityData = sqlContext.read.json("/zeppelin/data/cities.json").as[CityData]
cityData.write.format("com.mongodb.spark.sql").mode("overwrite").option("uri", "mongodb://mongodb:27017/sparkDemo.tsp").save()
z.angularBind("locations", cityData.limit(50).collect())

// Utility helpers
val scale  = (10 to 100 by 10).map(i => (s"$i", s"$i"))
def toInt(s: Object): Int = {
  try {
    s.toString.toInt
  } catch {
    case e: Exception => 0
  }
}

import scala.concurrent.duration.{Deadline, Duration}
def timed[R](block: => R): (R, Duration) = {
  val start = Deadline.now
  val result = block    // call-by-name
  val end = Deadline.now
  (result, end - start)
}

case class RouteStats(cities: Int, partitions: Int, populationSize: Int, maxGeneration: Int, maxSimilarity: Double, elitism: Double, seconds: Long, distance: Int)
val runs = scala.collection.mutable.ListBuffer[RouteStats]()

// Create a city class
case class City(name: String, country: String, lat: Double, lon: Double) {

  def distanceTo(that: City): Double = {
    val theta = this.lon - that.lon
    var dist = Math.sin(deg2rad(this.lat)) * Math.sin(deg2rad(that.lat)) +
      Math.cos(deg2rad(this.lat)) * Math.cos(deg2rad(that.lat)) *
        Math.cos(deg2rad(theta))
    dist = Math.acos(dist)
    dist = rad2deg(dist)
    dist = dist * 60 * 1.1515
    dist = dist * 1.609344
    dist
  }

  private def deg2rad(deg: Double): Double = deg * Math.PI / 180.0
  private def rad2deg(rad: Double): Double = rad * 180 / Math.PI
}

// Create a tour - a route between the cities
case class Tour(cities: Seq[City], path: Seq[Int], distance: Double)

// Create a helper
object Tour {
  import scala.util.Random

  def apply(cities: Seq[City]): Tour = {
    val path = Random.shuffle(cities.indices.toList)
    Tour(cities, path)
  }

  def apply(cities: Seq[City], path: Seq[Int]): Tour = {
    val distance = path.zip(path.tail).map(i => cities(i._1).distanceTo(cities(i._2))).sum
    new Tour(cities, path, distance)
  }

}

object Solver extends Serializable {
  import scala.util.Random
  val RANDOM = new Random()

  /**
   * The main method used to run a solving session.
   * This can be used in the `.map` function in an RDD to perform the calculations.
   *
   * @return this object.
   */
  def evolve(tours: Seq[Tour], maxGenerations: Int, maxSimilarity: Double, elitism: Double, mutationRate: Double): Seq[Tour] = {
    require(maxGenerations > 0, s"maxGenerations ($maxGenerations) must be greater than 0")
    require(maxSimilarity >= 0 && maxSimilarity <= 1, s"maxSimilarity ($maxSimilarity) must be between 0 and 1")
    require(elitism >= 0 && elitism <= 1, s"elitism ($elitism) must be between 0 and 1")
    require(mutationRate >= 0 && mutationRate <= 1, s"mutationRate ($mutationRate) must be between 0 and 1")

    var currGeneration = tours
    var bestGeneration = currGeneration
    var (i, noImprovement) = (1, 0)
    while (i < maxGenerations && noImprovement < 2) {
      // Save the best of the last generation
      val elites = currGeneration.take(Math.round(elitism * currGeneration.size).toInt)

      // Create new children and mutate the gene pool
      val children = currGeneration.takeRight(currGeneration.size - elites.size)
                        .map(tour => cross(tour, currGeneration, maxSimilarity))
                        .map(t =>if (mutationRate == 1.0 || RANDOM.nextDouble() < mutationRate) mutate(t) else t)

      currGeneration = (elites ++ children).sortBy(_.distance)

      if (currGeneration.head.distance < bestGeneration.head.distance) {
        bestGeneration = currGeneration
      } else {
        noImprovement += 1
      }
      i += 1
    }
    bestGeneration.map(t => Tour(t.cities, t.path :+ t.path.head))
  }

  /**
   * Return the fittest tour
   *
   * Used as a `reduce` method
   */
  def fittest(a: Tour, b: Tour): Tour = Seq(a, b).sortBy(_.distance).head

  /**
   * Crosses two tours, to create a new one
   * 
   * Uses a Greedy Crossover pattern picking parents nearest neighbours
   */
  private def cross(x: Tour, tours: Seq[Tour], maxSimilarity: Double): Tour = {
    val cities = x.cities
    val pathSize = x.path.size

    // Find a suitable partner to crossover within the maxSimilarity bounds
    val tourPairs = x.path.zip(x.path.tail)
    val y = tours.collectFirst({
      case t if tourPairs.intersect(t.path.zip(t.path.tail)).size.toDouble / pathSize < maxSimilarity => t
    }).getOrElse(tours(RANDOM.nextInt(tours.size)))

    // Create a Map which contains each path point as the key and the neighbouring points as the value
    val tourX = x.path.sliding(3).map(l => (l(1), (l.head, l(2)))).toMap
    val tourY = y.path.sliding(3).map(l => (l(1), (l.head, l(2)))).toMap

    // Pick a random start position
    var currentPos = x.path(RANDOM.nextInt(pathSize - 1))
    val nextGenerationPath = scala.collection.mutable.ListBuffer[Int]() += currentPos
    var citiesAvailable = x.path.filter(i => i != currentPos)

    while(citiesAvailable.nonEmpty) {
      val currentCity = cities(currentPos)
      val xCity = pickNearestTo(currentCity, pickNeighbours(citiesAvailable, tourX.get(currentPos)), cities)
      val yCity = pickNearestTo(currentCity, pickNeighbours(citiesAvailable, tourY.get(currentPos)), cities)
      currentPos = pickNearestTo(currentCity, (xCity, yCity), cities)
      nextGenerationPath += currentPos
      citiesAvailable = citiesAvailable.filter(i => i != currentPos)
    }
    Tour(cities, nextGenerationPath)
  }

  /**
   * Mutates the tour via 2opt
   */
  private def mutate(tour: Tour): Tour = {
    var optimizedPath = tour.path
    val pathSize = optimizedPath.size
    for (i <- 2 until pathSize - 1) {
      for (k <- i + 1 until pathSize) {
        // 1. take route[0] until route[i]
        val firstPart = optimizedPath.take(i)
        // 2. take route[i] until route[k] and reverse
        val secondPart = optimizedPath.slice(i, k)
        // 3. take route[k] to end
        val thirdPart = optimizedPath.slice(k, pathSize)

        val a = tour.cities(firstPart.last)
        val b = tour.cities(secondPart.last)
        val c = tour.cities(firstPart.head)
        val d = tour.cities(secondPart.head)

        val ori = Seq((a, b), (c, d)).map(c => c._1.distanceTo(c._2))
        val switched = Seq((a, c), (b, d)).map(c => c._1.distanceTo(c._2))
        if (switched.head < ori.head || switched.last < ori.last) {
          optimizedPath = firstPart ++ secondPart ++ thirdPart
        }
      }
    }
    Tour(tour.cities, optimizedPath)
  }

  private def pickNeighbours(citiesAvailable: Seq[Int], neighbours: Option[(Int, Int)]): (Int, Int) = {
    val alternative = citiesAvailable(RANDOM.nextInt(citiesAvailable.size))
    neighbours match {
      case Some(n) =>
        val first = if (citiesAvailable.contains(n._1)) n._1 else alternative
        val second = if (citiesAvailable.contains(n._2)) n._2 else alternative
        (first, second)
      case None => (alternative, alternative)
    }
  }

  private def pickNearestTo(city: City, choices: (Int, Int), cities: Seq[City]): Int = {
    val distA = city.distanceTo(cities(choices._1))
    val distB = city.distanceTo(cities(choices._2))
    if (distA < distB) choices._1 else choices._2
  }

}

// Uses toInt and scale set previously in the loading the data paragraph
// Number of cities to visit
val NUMBER_OF_CITIES_TO_VISIT = toInt(z.input("Number of cities to visit", 50))
// Number of partitions
val PARTITIONS = toInt(z.input("Number of partitions", 4))
// Size of the population of tours
val POPULATION_SIZE = toInt(z.input("Population size", 150))
// The maximum number of generations
val MAX_GENERATION =  toInt(z.input("Max generation", 50))
// The max similarity between tours for crossover
val MAX_SIMILARITY = toInt(z.select("Max similarity %", "70", scale))/100.0
// The max similarity between tours for crossover
val MUTATION_RATE = toInt(z.select("Mutation rate %", "50", scale))/100.0
// The number of the previous generation to potentially keep
val ELITISM =  toInt(z.select("Elitism %", "10", scale))/100.0

// Load the data from MongoDB
val cities =  spark.read.options(readConfig.asOptions).format("com.mongodb.spark.sql").load().as[City].limit(NUMBER_OF_CITIES_TO_VISIT).collect().toSeq

// 
val (route, duration) = timed({
    sc.parallelize((1 to PARTITIONS).map(i => (1 to POPULATION_SIZE).map( i => Tour(cities))), PARTITIONS)
  .flatMap(tour => Solver.evolve(tour, MAX_GENERATION, MAX_SIMILARITY, ELITISM, MUTATION_RATE))
  .reduce((a: Tour, b: Tour) => Solver.fittest(a, b))
})

