<script type="text/javascript">
function initMapRoute() {
    var map = L.map('mapRoute', { zoomControl:false, scrollWheelZoom: false, dragging: false, zoomAnimation: false }).setView([49.83798, 9.84375], 4);
    var Esri_WorldStreetMap = L.tileLayer('http://server.arcgisonline.com/ArcGIS/rest/services/World_Street_Map/MapServer/tile/{z}/{y}/{x}', {
  	    attribution: 'Tiles &copy; Esri &mdash; Source: Esri, DeLorme, NAVTEQ, USGS, Intermap, iPC, NRCAN, Esri Japan, METI, Esri China (Hong Kong), Esri (Thailand), TomTom, 2012'
    });
    Esri_WorldStreetMap.addTo(map);

    var header = angular.element($('#latestRun').parent('.ng-scope'));
    var el = angular.element($('#mapRoute').parent('.ng-scope'));
    var geoMarkers = L.layerGroup().addTo(map);
    var polylineLayer = L.layerGroup().addTo(map);
    angular.element(el).ready(function() {
        window.locationWatcherRoutes = el.scope().compiledScope.$watch('mapRoutePoints', function(newValue, oldValue) {
            geoMarkers.clearLayers(); // -- if you want to only show new data clear the layer first
            polylineLayer.clearLayers();
            var points = []
            angular.forEach(newValue, function(seat) {
                var marker = L.marker([seat.lat, seat.lon])
                  .bindPopup(seat.name)
                  .addTo(geoMarkers);
                this.push([seat.lat,seat.lon]);
            }, points);
            var polyline = L.polyline(points, { color: 'blue', weight: 4 });
            polylineLayer.addLayer(polyline);
            polyline.bringToFront();
        })

        var headerScope = header.scope().compiledScope;
        headerScope.$watch('latestRun', function(newValue, oldValue) {
            headerScope.cities = newValue.cities;
            headerScope.partitions = newValue.partitions;
            headerScope.maxGeneration = newValue.maxGeneration;
            headerScope.maxSimilarity = newValue.maxSimilarity;
            headerScope.elitism = newValue.elitism;
            headerScope.seconds = newValue.seconds;
            console.log(headerScope)
        })
    });
}

if (window.locationWatcherRoutes) {
    // clear existing watcher otherwise we'll have duplicates
